for url in urls:
    print(url)
    res = urllib.request.urlopen(url)
    soup = BeautifulSoup(res,'lxml')
    book_div = soup.find(attrs={'id':"topics"})
    if book_div != None:
        title = book_div.find(attrs={"id":'cb_post_title_url'})
        content = book_div.find(attrs={"id":"cnblogs_post_body"})
        file_name = str(title.string)[0:20]
        file_name = validateTitle(file_name)+".txt"
        f = open(file_name,"w",encoding='utf8')
        f.write(str(title.string) + "\n"+str(content.get_text())+"\n")
        f.close()
        print("保存成功")
